INSERT INTO `locales_source` VALUES ('3177','','default','!name must be a hexadecimal color value.','','none'),('3178','','default','Width and height can not both be blank.','','none'),('3179','','default','The part of the image that will be retained during the crop.','','none'),('3180','','default','The background color to use for exposed areas of the image. Use web-style hex colors (#FFFFFF for white, #000000 for black). Leave blank for transparency on image types that support it.','','none'),('3181','','default','There are currently no styles. <a href="!url">Add a new one</a>.','','none'),('3182','','default','There are currently no effects in this style. Add one by selecting an option below.','','7.43'),('3183','','default','view actual size','','7.43'),('3184','','default','Sample original image','','7.43'),('3185','','default','Sample modified image','','7.43'),('3186','','default','Resizing will make images an exact set of dimensions. This may cause images to be stretched or shrunk disproportionately.','','7.43'),('3187','','default','Scaling will maintain the aspect-ratio of the original image. If only a single dimension is specified, the other dimension will be calculated.','','7.43'),('3188','','default','Scale and crop','','7.43'),('3189','','default','Scale and crop will maintain the aspect-ratio of the original image, then crop the larger dimension. This is most useful for creating perfectly square thumbnails without stretching the image.','','7.43'),('3190','','default','Cropping will remove portions of an image to make it the specified dimensions.','','7.43'),('3191','','default','Desaturate','','7.43'),('3192','','default','Desaturate converts an image to grayscale.','','7.43'),('3193','','default','Rotating an image may cause the dimensions of an image to increase to fit the diagonal.','','7.43'),('3194','','default','Image resize failed using the %toolkit toolkit on %path (%mimetype, %dimensions)','','none'),('3195','','default','Image scale failed