INSERT INTO `locales_source` VALUES ('3601','','default','The %theme theme has been disabled.','','none'),('3602','','default','Please note that the administration theme is still set to the %admin_theme theme; consequently, the theme on this page remains unchanged. All non-administrative sections of the site, however, will show the selected %selected_theme theme by default.','','none'),('3603','','default','%theme is now the default theme.','','7.43'),('3604','','default','User verification status in comments','','7.43'),('3605','','default','These settings only exist for the themes based on the %engine theme engine.','','none'),('3606','','default','The logo could not be uploaded.','','none'),('3607','','default','The favicon could not be uploaded.','','none'),('3608','','default','The custom logo path is invalid.','','none'),('3609','','default','The custom favicon path is invalid.','','none'),('3610','','default','@module (<span class="admin-missing">incompatible with</span> version @version)','','none'),('3611','','default','This version is not compatible with Drupal !core_version and should be replaced.','','none'),('3612','','default','Would you like to continue with the above?','','7.43'),('3613','','default','Enter a valid IP address.','','none'),('3614','','default','This IP address is already blocked.','','none'),('3615','','default','You may not block your own IP address.','','none'),('3616','','default','The IP address %ip has been blocked.','','none'),('3617','','default','Are you sure you want to delete %ip?','','none'),('3618','','default','The IP address %ip was deleted.','','none'),('3619','','default','How this is used depends on your site''s theme.','','7.43'),('3620','','default','Number of posts on front page','','7.43'),('3621','','default','The maximum number of posts displayed on overview pages such as the front page.','','7.43'),('3622','','default','This page is displayed when the requested document is denied to the current user. Leave blank to display a generic "access denied" page.','','7.43'),('3623','','default','This page is displayed when no other content matches the requested document. Leave blank to display a generic "page not found" page.','','7.43');
INSERT INTO `locales_source` VALUES ('3624','','default','Errors and warnings','','none'),('3625','','default','Clear all caches','','none'),('3626','','default','<strong class="error">Set up the <a href="!file-system">public files directory</a> to make these optimizations available.</strong>','','none'),('3627','','default','Bandwidth optimization','','none'),('3628','','default','Compress cached pages.','','none'),('3629','','default','Public file system path','','7.43'),('3630','','default','A local file system path where public files will be stored. This directory must exist and be writable by Drupal. This directory must be relative to the Drupal installation directory and be accessible over the web.','','7.43'),('3631','','default','Private file system path','','7.43'),('3632','','default','A local file system path where temporary files will be stored. This directory should not be accessible over the web.','','7.43'),('3633','','default','Default download method','','7.43'),('3634','','default','This setting is used as the preferred download method. The use of public files is more efficient, but does not provide any access control.','','7.43'),('3635','','default','No image toolkits were detected. Drupal includes support for <a href=''!gd-link''>PHP''s built-in image processing functions</a> but they were not detected on this system. You should consult your system administrator to have them enabled, or try using a third party toolkit.','','none'),('3636','','default','Description of your site, included in each feed.','','none'),('3637','','default','Time zones','','none'),('3638','','default','Users may set their own time zone.','','none'),('3639','','default','Remind users at login if their time zone is not set.','','none'),('3640','','default','Only applied if users may set their own time zone.','','none'),('3641','','default','Time zone for new users','','none'),('3642','','default','Default time zone.','','none'),('3643','','default','Empty time zone.','','