INSERT INTO `locales_source` VALUES ('3646','','default','Add date type','','7.43'),('3647','','default','The date type must contain only alphanumeric characters and underscores.','','none'),('3648','','default','This date type already exists. Enter a unique type.','','none'),('3649','','default','New date type added successfully.','','none'),('3650','','default','Put site into maintenance mode','','none'),('3651','','default','Maintenance mode message','','none'),('3652','','default','Message to show visitors when the site is in maintenance mode.','','none'),('3653','','default','Use URLs like <code>example.com/user</code> instead of <code>example.com/?q=user</code>.','','none'),('3654','','default','Requires: !module-list','','7.43'),('3655','','default','Required by: !module-list','','7.43'),('3656','','default','Are you sure you want to remove the date type %type?','','none'),('3657','','default','Removed date type %type.','','none'),('3658','','default','No custom date formats available. <a href="@link">Add date format</a>.','','7.43'),('3659','','default','Displayed as','','7.43'),('3660','','default','Displayed as %date','','none'),('3661','','default','A user-defined date format. See the <a href="@url">PHP manual</a> for available options.','','7.43'),('3662','','default','Save format','','none'),('3663','','default','This format already exists. Enter a unique format string.','','none'),('3664','','default','Custom date format updated.','','none'),('3665','','default','Custom date format added.','','none'),('3666','','default','Available actions:','','7.43'),('3667','','default','Create an advanced action','','7.43'),('3668','','default','A unique label for this advanced action. This label will be displayed in the interface of modules that integrate with actions, such as Trigger module.','','7.43'),('3669','','default','Deleted %ip','','none'),('3670','','default','Enabled theme','','none'),('3671','','default','Enabled themes','','7.43'),('3672','','default