INSERT INTO `locales_source` VALUES ('4074','','default','Image style','','7.43'),('4075','','default','1 pending update (@number_applied to be applied, @number_incompatible skipped)','','none'),('4076','','default','@count pending updates (@number_applied to be applied, @number_incompatible skipped)','','none'),('4077','','default','The Blog module allows registered users to maintain an online journal, or <em>blog</em>. Blogs are made up of individual <em>blog entries</em>. By default, the blog entries are displayed by creation time in descending order, with comments enabled, and are promoted to the site''s front page. For more information, see the online handbook entry for <a href=''@blog''>Blog module</a>.','','none'),('4078','','default','Author textfield','','7.43'),('4079','','default','Subject textfield','','7.43'),('4080','','default','A contact form with category %category already exists.','','none'),('4081','','default','Hello !recipient-name,','','none'),('4082','','default','Attempt to create an instance of a field @field_name that doesn''t exist or is currently inactive.','','none'),('4083','','default','Attempt to create an instance of field @field_name on bundle @bundle that already has an instance of that field.','','none'),('4084','','default','Field API to add fields to entities like nodes and users.','','7.43'),('4085','','default','Overview of fields on all entity types.','','7.43'),('4086','','default','Configuring text formats','','7.43'),('4087','','default','Configure text formats on the <a href="@formats">Text formats page</a>. <strong>Improper text format configuration is a security risk</strong>. To ensure security, untrusted users should only have access to text formats that restrict them to either plain text or a safe set of HTML tags, since certain HTML tags can allow embedding malicious links or scripts in text. More trusted registered users may be granted permission to use less restrictive text formats in order to create rich content.','','7.43'),('4088','','default','Users with access to more than one text format can use the <em>Text format</em> fieldset to choose between available text formats when creating or editing multi-line content. Administrators can define the text formats available to each user role, and control the order of formats listed in the <em>Text format</em> fieldset on the <a href="@text-formats">Text formats page</a>.','','7.43');
INSERT INTO `locales_source` VALUES ('4089','','default','Text formats define the HTML tags, code, and other formatting that can be used when entering text. <strong>Improper text format configuration is a security risk</strong>. Learn more on the <a href="@filterhelp">Filter module help page</a>.','','none'),('4090','','default','Show more content','','7.43'),('4091','','default','Collapse','','none'),('4092','','default','The testing framework requires the DOMDocument class to be available. Check the configure command at the <a href="@link-phpinfo">PHP info page</a>.','','none'),('4093','','default','Error messages to display','','none'),('4094','','default','It is recommended that sites running on production environments do not display any errors.','','none'),('4095','','default','Cannot open %file_path','','none'),('4096','','default','Term name textfield','','7.43'),('4097','','default','Term description textarea','','7.43'),('4098','','default','Password reset instructions will be mailed to %email. You must log out to use the password reset link in the e-mail.','','none'),('4099','','default','Enter your current password to change the %mail or %pass. !request_new.','','7.43'),('4100','','default','Current password','','7.43'),('4101','','default','Your current password is missing or incorrect; it''s required to change the %name.','','none'),('4102','','default','A welcome message with further instructions has been e-mailed to the new user <a href="@url">%name</a>.','','none'),('4103','','default','A welcome message with further instructions has been sent to your e-mail address.','','none'),('4104','','default','Configure user accounts.','','7.43'),('4105','','default','Configure default behavior of users, including registration requirements, e-mails, fields, and user pictures.','','7.43'),('4106','','default','%module module installed.','','none'),('4107','','default','Use this page to create a new custom block.','','7.43'),('4108','','default','Number of recen